from web3 import Web3
import json 

class deploy:

    def __init__(self):
        
        ganache_url = "HTTP://127.0.0.1:7545"
        
        self.web3 = Web3(Web3.HTTPProvider(ganache_url))

        self.abi = json.loads('[{"inputs":[],"stateMutability":"nonpayable","type":"constructor"},{"inputs":[{"internalType":"uint256","name":"","type":"uint256"}],"name":"candidates","outputs":[{"internalType":"uint256","name":"id","type":"uint256"},{"internalType":"string","name":"name","type":"string"},{"internalType":"uint256","name":"voteCount","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"candidatesCount","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"end","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"owner","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"totalVote","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"_candidateId","type":"uint256"}],"name":"vote","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"","type":"address"}],"name":"voters","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"view","type":"function"}]')
        self.bytecode= ""
  
    def checkConnected(self):
        print(self.web3.isConnected())

    def deployContract(self , owner , signature):
        self.election = self.web3.eth.contract(abi=self.abi , bytecode=self.bytecode)
            
        self.transaction_body = {
            'nonce':self.web3.eth.get_transaction_count(owner),
            'value':self.web3.toWei(10 , 'ether'),
            'gas'   :1728712,
            'gasPrice':self.web3.toWei(8 , 'gwei')
        }

        deployment = self.election.constructor().buildTransaction(self.transaction_body)
        signed_transaction = self.web3.eth.account.sign_transaction(deployment , signature)
        result = self.web3.eth.send_raw_transaction(signed_transaction.rawTransaction)
        # return result

    
    def totalVote(self):
        data = self.election.functions.TotalVote().call()

